public class Sum_of_number {

	/**
	 * @param args
	 */
	public static void m1(int n){
		int Sum =0;
		for(int j =0; j<n;j++){
			Sum=Sum+j;
			}
		System.out.println(Sum);
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Sum_of_number.m1(10);
	}

}
